import React from 'react';
import axios from 'axios';

class RestController extends React.Component {

  state = {
    posts: [],
    comments: []
  };


  componentDidMount() {
    axios.get(`http://localhost:9000/posts`).then(res => {
      const posts = res.data;
      this.setState({ posts });
    });

    axios.get(`http://localhost:9000/posts/comments`).then(res => {
      const comments = res.data;
      this.setState({ comments });
    });
  }

  render() {
        return (
            <div className="container">
                <div className="jumbotron-div col s12">
                    <ul className="collection">
                        {this.state.posts.map(post =>
                            (
                            	// <h1>Posts Extrated from Json Placeholder API</h1>
                                <div className="post-card" key={post.id}>
                                	<h2> { post.title.substring(0, 50).concat('...') } </h2>
                                	<p> { post.body.substring(0, 200).concat('...') } </p>                                	

                                	<div className="jumbotron-div col s12">
					                    <ul className="collection">
					                        {this.state.comments.map(comment =>
					                            (
					                            	// <h1>Posts Extrated from Json Placeholder API</h1>
					                                <div className="comment-card" key={comment.id} >					                                	
					                                	<p> { comment.body.substring(0, 200).concat('...') } </p>
					                                	<h2> { comment.name.substring(0, 50).concat('...') } </h2>                                	
					                                	<h3> { comment.email.substring(0, 50).concat('...') }  </h3>
					                                </div>                               
					                            ))
					                        }
					                    </ul>
					                </div> 
                                </div>
                            ))
                        }
                    </ul>
                </div>
            </div>
        );
    }

}

export default RestController;
