import React from 'react';
import axios from 'axios';

class RestController extends React.Component {

  state = {
    posts: [],
    comments: []
  };


  componentDidMount() {
    axios.get(`http://localhost:9000/posts`).then(res => {
      const posts = res.data;
      this.setState({ posts });
    });

    axios.get(`http://localhost:9000/posts/comments`).then(res => {
      const comments = res.data;
      this.setState({ comments });
    });
  }

  render() {
        return (
            <div className="container">
                <div className="jumbotron-div col s12">
                    <ul className="collection">
                        {this.state.posts.map(post =>
                            (
                                <div className="card">
                                	<h2> 
                                	   { post.title.substring(0, 50).concat('...') }
                                	   <p> { post.body.substring(0, 200).concat('...') } </p>
                                	</h2>
                                </div>
                            )
                            )
                        }
                    </ul>
                </div>
            </div>
        );
    }

}

export default RestController;
